import React, {useState} from 'react';
import { DataGrid, GridColDef, GridSelectionModel } from '@mui/x-data-grid';
import { serverCalls } from '../../api';
import { useGetData } from '../../custom-hooks';
import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle
} from '@mui/material'
import { DroneForm } from '../../components';

const columns: GridColDef[] = [
  { field: 'id', headerName: 'ID', flex: 1, minWidth: 130 },
  {
    field: 'name',
    headerName: 'Drone',
    width: 150,
    editable: true,
  },
  {
    field: 'description',
    headerName: 'Description',
    width: 150,
    editable: true,
  },
  {
    field: 'price',
    headerName: 'Price',
    type: 'number',
    width: 110,
    editable: true,
  },
  {
    field: 'cost_of_production',
    headerName: 'Cost of Production',
    type: 'number',
    width: 110,
    editable: true,
  },
  {
    field: 'max_speed',
    headerName: 'Max Speed',
    type: 'number',
    width: 110,
    editable: true,
  },
  {
    field: 'series',
    headerName: 'Series',
    type: 'number',
    width: 110,
    editable: true,
  },
  {
    field: 'weight',
    headerName: 'Weight',
    type: 'number',
    width: 110,
    editable: true,
  },
  {
    field: 'flight_time',
    headerName: 'Flight Time',
    type: 'number',
    width: 110,
    editable: true,
  },
  {
    field: 'dimensions',
    headerName: 'Dimensions',
    type: 'number',
    width: 110,
    editable: true,
  },
  {
    field: 'camera_quality',
    headerName: 'Camera',
    type: 'number',
    width: 110,
    editable: true,
  },

];

interface gridData {
  data: {
    id?:string;
  }
}

export const DataTable = () => {
 let { droneData, getData } = useGetData();
 let [open, setOpen] = useState(false);
 let [gridData, setData] = useState<GridSelectionModel>([])

 let handleOpen = () => {
   setOpen(true);
 }

 let handleClose = () => {
  setOpen(false);
}

let deleteData = async () => {
  await serverCalls.delete(`${gridData[0]}`)
  getData();
}

console.log(gridData) // A list of IDs from checked rows

  return (
      <div style={{ height: 400, width: '100%' }}>
          <h2>Drones In Inventory</h2>
        <DataGrid
          rows={droneData}
          columns={columns}
          pageSize={5}
          rowsPerPageOptions={[5]}
          checkboxSelection
          disableSelectionOnClick
          onSelectionModelChange={newSelectionModel => setData(newSelectionModel)}
          {...droneData}
        />
        <Button onClick={handleOpen} color='primary'>Update</Button>
        <Button onClick={deleteData} color='warning'>Delete</Button>
        <Dialog open={open} onClose={handleClose} aria-labelledby='form-dialog-title'>
          <DialogTitle id='form-dialog-title'>Update a Drone</DialogTitle>
          <DialogContent>
            <DialogContentText>Updating Drone ID: {gridData[0]}</DialogContentText>
          <DroneForm id={`${gridData[0]}`} />
          </DialogContent>
          <DialogActions>

          </DialogActions>
        </Dialog>
      </div>
    );
  }
