import React from 'react';
import { useDispatch, useSelector, useStore } from 'react-redux'
import {useForm} from 'react-hook-form'
import { Button } from '@mui/material'
import { chooseName, choosePrice, chooseCam, chooseDescription, chooseFlight, chooseSpeed } from '../../redux/slices/rootSlice'
import { Input } from '../sharedComponents';
import { serverCalls } from '../../api'
import { useGetData } from '../../custom-hooks'

interface DroneFormProps{
    id?:string;
    data?:{}
}

interface DroneState {
    name:string,
    price:string;
    description:string;
    camera_quality:string;
    flight_time:string;
    max_speed:string;
    cost_of_production:string;
    weight:string;
    series:string;
}

export const DroneForm = (props:DroneFormProps) => {
    const dispatch = useDispatch();
    let {droneData, getData} = useGetData();
    const store = useStore();
    const name = useSelector<DroneState>(state => state.name)
    const price = useSelector<DroneState>(state => state.price)

    const { register, handleSubmit } = useForm({})

    const onSubmit = async (data:any, event:any) => {
        console.log(props.id)

        if(props.id!) {
            await serverCalls.update(props.id!, data)
            console.log(`Updated: ${data.name} \n ID: `)
        }
    }
}
