import {
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle
} from '@mui/material'
import { DroneForm } from '../../components';

const columns: GridColDef[] = [
  { field: 'id', headerName: 'ID', flex: 1, minWidth: 130 },
  {
    field: 'name',
    headerName: 'Drone',
    width: 150,
    editable: true,
